package constructor_study;

public class Variablecall1 {
static int value=500;//static 
int mark=56;//non static 
static String name="jayash";//static 


	public static void main(String[] args) {
		// TODO Auto-generated method stub
Variablecall1 kk=new  Variablecall1();
System.out.println("value is "+value +"\n mark is "+kk.mark +"\n name is "+name);
	}

}
