package loops;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// I want to print "1" five times
		//syntax ;-->for(initializing;condition;increment/decrement)
		
		//from where to start--->initializing
		//condition---><=5//>=1
		//1..2..3..4..5-->increment,5..4..3..2..1-->decrement
		for(int i=0;i<=10;i++)
		{
			System.out.println("k");
		}
		for(int n=6;n<=10;n++)
		{
			System.out.println("raj");
		}
	}

}
